// Packages
import { Link } from 'react-router-dom';

// Graphics
import homeIcon from './../assets/graphics/home-icon.svg'
import profileIcon from './../assets/graphics/profile-icon.svg'

export default function Layout({ children, currentUser }) {
  return (
    <div>
      {children}
      <footer>
        {!currentUser ? (
          <div>
            <p>Don't have an account? <Link to='/sign-up'>Sign Up.</Link></p>
            <p>Already have an account? <Link to='/sign-in'>Sign In.</Link></p>
          </div>
        ) : (
          <div>
            <Link to=''><img src={homeIcon} alt='home icon' /></Link>
            <Link to=''><img src={profileIcon} alt='profile icon' /></Link>
          </div>
        )
        }
      </footer >
    </div>
  )
}
